"use strict";(self.webpackChunkada_cad_docs=self.webpackChunkada_cad_docs||[]).push([[9922],{10900:e=>{e.exports=JSON.parse('{"label":"cloth","permalink":"/docs/tags/cloth","allTagsPath":"/docs/tags","count":11,"items":[{"id":"reference/operations/chaos","title":"chaos sequence","description":"Made in collaboration Jacqueline Wernimont, Molly Morin and Nikki Stevens to explore non-deterministic drafts. Tiles the input drafts, randomly selecting which draft to place at which position. At each position, it randomly rotates the draft by either 90, 180 or 270 degrees.","permalink":"/docs/reference/operations/chaos"},{"id":"reference/operations/fill","title":"fill","description":"Fills black cells of the first input, \u201cpattern,\u201d with the draft specified by the second input, and the white cells with draft specified by the third input.","permalink":"/docs/reference/operations/fill"},{"id":"reference/operations/imagemap","title":"image map","description":"Uploads an image and creates an input for each color found in the image. Assigning a draft to the color fills the color region with the selected draft.","permalink":"/docs/reference/operations/imagemap"},{"id":"reference/operations/join_left","title":"join left","description":"Joins drafts assigned to the drafts input together horizontally.","permalink":"/docs/reference/operations/join_left"},{"id":"reference/operations/join_top","title":"join top","description":"Joins drafts assigned to the inlets vertically.","permalink":"/docs/reference/operations/join_top"},{"id":"reference/operations/weft_profile","title":"pattern across length","description":"Given a series of letters (a b c), this operation will associate a draft with each letter, and then arrange following the pattern order","permalink":"/docs/reference/operations/weft_profile"},{"id":"reference/operations/warp_profile","title":"pattern across width","description":"Given a series of letters (a b c, etc), this operation will associate a draft with each letter, and then arrange those drafts from left to right following the pattern order","permalink":"/docs/reference/operations/warp_profile"},{"id":"reference/operations/rectangle","title":"rectangle","description":"Generates an unfilled rectangle of the size defined by the parameters. If given an input, the rectangle fills with the input.","permalink":"/docs/reference/operations/rectangle"},{"id":"reference/operations/tile","title":"tile","description":"Repeats the input block along the warp and weft according to the parameters described below.","permalink":"/docs/reference/operations/tile"},{"id":"reference/operations/sample_length","title":"variable length sampler","description":"Given a series of letters and numbers (a100 b200 c300), this operation will associate a draft with each letter, and then arrange those drafts from top to bottom following the pattern order. The numbers next to each letter describe the number of pics upon which the specified pattern should be repeated","permalink":"/docs/reference/operations/sample_length"},{"id":"reference/operations/sample_width","title":"variable width sampler","description":"Given a series of letters and numbers (a100 b200 c300), this operation will associate a draft with each letter, and then arrange those drafts from left to right following the pattern order. The numbers next to each letter describe the number of ends upon which the specified pattern should be repeated","permalink":"/docs/reference/operations/sample_width"}],"unlisted":false}')}}]);